import axios from 'axios'
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllusers } from '../redux/userSlice'
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import UserdetailsTable from './UserdetailsTable';


function UsersList() {

    const dispatch = useDispatch()
    const users = useSelector((state) => state?.users?.users ?? [])


    useEffect(() => {
        const userLi = async () => {

            try {
                const res = await axios.get(`http://localhost:5000/users`, { withCredentials: true })
                if (res.data) {
                    dispatch(getAllusers(res?.data?.allUsers ?? []))
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }
        userLi()
    }, [])

    const deleteAccount = async (userId) => {
        console.log(userId);
        // try {
        //     const res = await axios.delete(`http://localhost:5000/deleteAccount/`, {
        //         withCredentials: true
        //     });

        //     if (res.data.success) {
        //         dispatch()

        //     } else {
        //         alert('Account deletion failed.');
        //     }
        // } catch (error) {
        //     alert(`Error during delete: ${error.message}`);
        // }
    };

    return (
        <div className='usersList'>
            <table className='users-table'>
                <tr>
                    <th>User Name</th>
                    <th>Email</th>
                    <th colSpan={2}>Status</th>
                    <th>Remove</th>

                </tr>
                {users && users.map((element,index)=>{
                    <tr i>
                        <UserdetailsTable userName={element.userName} email={element.email} userId={element._id}/>
                    </tr>
                })}
                {/* {users && users.map((element, index) => (
                    <tr key={index}>
                        <td>{element.userName}</td>
                        <td>{element.email}</td>
                        <td><Button variant="success" as={Link} onClick={deleteAccount(element._id)}>Active</Button></td>
                        <td><Button variant="dark" as={Link}>Inactive</Button></td>
                        <td><Button variant="danger" as={Link} >Remove</Button>{' '}</td>
                    </tr>
                ))} */}

            </table>
        </div>
    )
}

export default UsersList
