import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    user: '',
    isAuth: false,
    token: '',
    users:''

}

const usersList = createSlice({
    name: "userAuth",
    initialState,
    reducers: {
        userAuth: function (state, actions) {
            state.user = actions.payload;
            state.isAuth = true;
        },
        userUpdate: function (state, actions) {
            state.user = actions.payload;
          
        },
        deleteUser: function (state, actions) {
            state.user = actions.payload;
            
        },
       getAllusers:function(state,actions){
          state.users = actions.payload
        }
    }
});

export const { userAuth, userUpdate, deleteUser,getAllusers } = usersList.actions;
export default usersList.reducer;
