import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    user: '',
    isAuth:false,
    token:''

}

const usersList = createSlice({
    name: "usersList",
    initialState,
    reducers: {
        userDetails : function(state,actions){
            state.users = actions.payload
            state.isAuth
        }

    }
})

export const {userDetails} = usersList.actions
export default usersList.reducer
