import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllusers } from '../redux/userSlice'
import { Link, useNavigate } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import ToggleButton from 'react-bootstrap/ToggleButton';



function UsersList() {
    const [userStatus, setUserStatus] = useState(true);
    const [active, setActive] = useState({

    })


    console.log(userStatus);

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const users = useSelector((state) => state?.users?.users ?? [])
    const loggedUser = useSelector((state) => state.users.user.userDetails)

    const deleteAccount = async (id) => {
        try {
            const res = await axios.delete(`http://localhost:5000/deleteAccount/${id}`, {
                withCredentials: true
            });

            if (res.data.success) {

            } else {
                alert('Account deletion failed.');
            }
        } catch (error) {
            alert(`Error during delete: ${error.message}`);
        }
    };

    useEffect(() => {
        const userLi = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/users`, { withCredentials: true })
                if (res.data) {
                    dispatch(getAllusers(res?.data?.allUsers ?? []))
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }
        userLi()
    }, [])


    const userHandler = (id) => {
        try {
            const res = axios.put(`http://localhost:5000/userAcive/${id}`, { withCredentials: true })
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }


    const newUsers = Array.isArray(users) ? users.filter((newUser) => newUser._id !== loggedUser.userId) : [];



    return (
        <div className='usersList'>
            <table className='users-table'>
                <tr>
                    <th>User Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Remove</th>
                </tr>


                {newUsers && newUsers.map((element, index) => (

                    <tr key={index}>
                        <td>{element.userName}</td>
                        <td>{element.email}</td>
                        <td key={index}>


                            <ToggleButton
                                id={`toggle-check-${element._id}`}
                                type="checkbox"
                                variant={active[element._id] ? 'success' : 'danger'}
                                checked={active[element._id] === undefined ? true : active[element._id]}
                                onChange={(e) => {
                                    const isChecked = e.currentTarget.checked;
                                    setActive((prevActive) => ({
                                        ...prevActive,
                                        [element._id]: isChecked,
                                    }));
                                }}
                            >
                                {active[element._id] === undefined || active[element._id] ? 'Active' : 'Inactive'}
                            </ToggleButton>




                        </td>
                        <td><Button variant="danger" onClick={() => deleteAccount(element._id)}>Remove</Button></td>

                    </tr>
                ))}

            </table>
        </div>
    )
}

export default UsersList
