import React from 'react'

function Riviewsection() {
    const state = use
    const [reviews, setReviews] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [username, setUsername] = useState('');
    const [rating, setRating] = useState('');
    const [comment, setComment] = useState('');

    const showFormHandler = () => {
        setShowForm(true);
    };
    const submitReviewHandler = () => {
        if (username && rating && comment) {
            const newReview = {
                username: username,
                rating: rating,
                comment: comment,
            };

            setReviews([...reviews, newReview]);
            resetForm();
        }
    };

    const resetForm = () => {
        setUsername('');
        setRating('');
        setComment('');
        setShowForm(false);
    };
    return (
        <div>
            <div className="review-container">
                <h1>Product Reviews</h1>

                <div className="reviews">
                    {reviews.map((review, index) => (
                        <div key={index} className="review">
                            <h3>{review.username}</h3>
                            <p>
                                <strong>Rating:</strong> <span className="rating">{review.rating}</span>
                            </p>
                            <p>
                                <strong>Comment:</strong> {review.comment}
                            </p>
                        </div>
                    ))}
                </div>

                <button onClick={showFormHandler}>Add a Review</button>

                {showForm && (
                    <div className="review-form">
                        <h2>Add Your Review</h2>
                        <label>
                            Username:
                            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
                        </label>
                        <label>
                            Rating:
                            <input type="number" min="1" max="5" value={rating} onChange={(e) => setRating(e.target.value)} required />
                        </label>
                        <label>
                            Comment:
                            <textarea value={comment} onChange={(e) => setComment(e.target.value)} required></textarea>
                        </label>
                        <button onClick={submitReviewHandler}>Submit Review</button>
                    </div>
                )}
            </div>
        </div>
    )
}

export default Riviewsection
