import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    user: '',
    isAuth: false,
    token: ''

}

const usersList = createSlice({
    name: "userAuth",
    initialState,
    reducers: {
        userAuth: function (state, actions) {
            console.log("actions.payload", actions.payload);
            state.user = actions.payload.userDetails;
            state.isAuth = true;

        }
        ,
        userUpdate: function (state, actions) {
            state.user = actions.payload;
            state.isAuth = true;
        }

    }
})

export const { userAuth, userUpdate } = usersList.actions
export default usersList.reducer
