import React, { useState } from 'react'
import { useSelector } from 'react-redux';
import axios from 'axios';




function UserProfile() {
    const userData = useSelector((state) => state.users?.users?.userDetails ?? 'Not available');
    const [input, setInput] = useState({
        userName: "",
        password: "",
    })
    const [formErr, formSetErr] = useState({});
    const [isSubmited, setSubmit] = useState(false)


    const HandleInputChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setInput({ ...input, [name]: value });


    }
    console.log(input);




    const validateForm = (values) => {
        const errors = {};
        var usernamePattern = /^[a-zA-Z]{5,20}_?[a-zA-Z]*$/;
        var passwordPattern = /^\d{8}$/;
        if (!values.userName) {
            errors.userName = "Username is required"
        }
        else if (!usernamePattern.test(values.userName)) {
            errors.userName = "Username must be 3-20 characters and can only contain letters"
        }
        if (!values.password) {
            errors.password = "Enter 8 digit"
        }
        else if (!passwordPattern.test(values.password)) {
            errors.password = "password is required"
        }

        return errors
    }


    const submitHandler = (e) => {
        e.preventDefault();
        formSetErr(validateForm(input));
        setSubmit(true)
        formSumit()
    }
    const formSumit = async () => {
        if (Object.keys(formErr).length === 0 && isSubmited) {

            try {
                const res = await axios.post("http://localhost:5000/profileUpdate", input);

            }
            catch {
                alert("Error during signup:', error")
            }
        }
    }

    return (
        <div>
            <div className='Signup'>
                <div className='Signup-sub'>

                    <form action="" onSubmit={submitHandler}>
                        <div className='input-sty-prnt'>
                            <div className='sing-head'>
                                <h1>Edit profile</h1>
                            </div>

                            <div className='input-sty'>
                                <label htmlFor="" style={{ 'textAlign': 'center' }}><b>User name</b></label>
                                <div className='img-inp'>

                                    <input type="text"
                                        name='userName'
                                        value={input.userName}
                                        defaultValue={userData.userName}
                                        onChange={HandleInputChange}

                                    />
                                </div>

                            </div>
                            <span className='error-message'>{formErr.userN}</span>

                            <div className='input-sty'>
                                <label htmlFor="" style={{ 'textAlign': 'center' }}><b>Email</b></label>
                                <div className='img-inp'>

                                    <input type="text" name='email'
                                        value={input.userEmail}
                                        defaultValue={userData.userEmail}
                                        onChange={HandleInputChange}



                                    />
                                </div>
                            </div>


                        </div>
                        <div className='endSection'>
                            <button type='submit' className='Singup'><b>Edit</b></button>
                            {/* <p>Already have an account? <Link to={'/login'}><b>Login</b></Link></p> */}
                        </div>
                    </form>
                </div>

            </div>
        </div>
    )
}

export default UserProfile
