import { Container, Row, Col, Button } from 'react-bootstrap'
import background from '../images/pexels-chan-walrus-958545 (1).jpg'
import Cards from './Cards'
import { useEffect, useState } from 'react'



function Home() {

    useEffect(() => {
        fetch("./hotelDetails/restaurants.json")
            .then((details) => details.json())
            .then((data) => setDetails(data));
    }, [])



    const [details, setDetails] = useState('')
    // console.log(details);



    // console.log(state);



    return (
        <div>
            <Container fluid>
                <Row>
                    <Col className='p-0'>
                        <div className='background-img'>
                            <img src={background} alt="" />
                            <div className='content-back'>
                            <div className='abslt-cnt'>

                                <h2>Let food be thy medicine and   <br /> medicine
                                    be thy food</h2>
                                <h5>
                                    Although the skills arenâ€™t hard to learn, finding the happiness <br /> and finding the satisfaction and finding fulfillment <br /> in continuously serving somebody else something good to eat
                                </h5>
                            </div>
                            <div>
                                <Button variant="warning">Book online</Button>{' '}
                                <Button variant="outline-warning">See More</Button>{' '}
                            </div>

                        </div>
                        </div>
                        

                    </Col>
                </Row>
                <Row>
                {details &&  details.restaurants.map((element,index)=>(
                       <Col lg={4} md={6} key={index}>
                          <div className='card-par'>
                          <Cards name = {element.name} img ={element.photograph} neighborhood ={element.neighborhood} Address ={element.address}/>
                          </div>
                       </Col>
                    ))}
                       
                    
                </Row>
               

            </Container >
        </div>
    )
}

export default Home
