import React, { useState } from 'react'
import './style.css'
import user from './images/icons8-username-30.png'
import password from './images/icons8-password-24.png'
import { Link } from 'react-router-dom';

function AddresLogin() {

    const [input, setInput] = useState({

        userName: "",
        password: ""
    })

    const HandleSubmit = () => {
        validateForm(input)
    }

    const HandleInputChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setInput({ ...input, [name]: value });
    }

    const validateForm = (values) => {
        const errors = {};
        const userNamePattern = /^[a-zA-Z0-9_]+$/;
        const passwordPattern = /^.{8,}$/;

        if (!values.userName) {
            errors.userName = "Username is required"
        }
        else if (userNamePattern.test(values.userName)) {
            errors.userName = "Only letters and numbers, and it must be between 6 and 16 characters long"
        }

        if (!values.password) {
            errors.password = "password is required"
        }
        else if (passwordPattern.test(values.password)) {
            errors.password = "Enter 8 characters"
        }

        return errors
    }
    return (
        <div className='Signup'>
            <div className='Signup-sub'>
                <form action="" onsu>
                    <div className='input-sty-prnt'>
                        <div className='sing-head'>
                            <h1>Welcome Back</h1>
                            <h5>Enter your credential to login</h5>
                        </div>
                        <div className='input-sty'>
                            <label htmlFor="" style={{ 'textAlign': 'center' }}><b>User name</b></label>
                            <div className='img-inp'>
                                <img className='iconsLo' src={user} alt="" />
                                <input type="text"
                                    name='userName'
                                    onChange={HandleInputChange}
                                />
                            </div>
                        </div>
                        <div className='input-sty'>
                            <label htmlFor="" style={{ 'textAlign': 'center' }}><b>Password</b></label>
                            <div className='img-inp'>
                                <img className='iconsLo' src={password} alt="" />
                                <input type="text"
                                    name='password'
                                    onChange={HandleInputChange}
                                />
                            </div>
                        </div>
                    </div>
                    <div className='fgt-pass'>
                        <p><b>Forgot password?</b></p>
                    </div>
                    <div className='endSection'>
                        <button className='Singup'><b>Login</b></button>
                        <p>Dont have an account? <Link to={'/singup'}><b>Sing up</b></Link></p>
                    </div>
                </form>
            </div>

        </div>
    )
}

export default AddresLogin
