import axios from 'axios';
import React from 'react'
import { Card, Button } from 'react-bootstrap';
import { Link, useLocation } from "react-router-dom";


function Cards({ resName, photographs, neighborhood, location, id }) {
    const myLocation = useLocation();
    console.log(id);
    const resRemove = async () => {
        try {

            const deleteRes = await axios.delete(`http://localhost:5000/resRemove/${id}`, { withCredentials: true });
            console.log('Response:', deleteRes.data);
        } catch (error) {
            alert(error.message);
        }
    };
    return (
        <div>
            <div className='card-child'>
                <Card style={{ width: '18rem', display: 'flex', alignItems: 'center' }}>
                    <Card.Img className='card-img' variant="top" src={`${process.env.REACT_APP_SERVER_URL}${photographs}`} />
                    <Card.Body>
                        <Card.Title>
                            <p style={{ textAlign: 'center' }}>
                                {resName}
                            </p>
                        </Card.Title>
                        <Card.Text>
                            <ul className='card-cnt'>
                                <li>Neighborhood:{neighborhood}</li>
                                <li>Location:{location}</li>
                            </ul>
                        </Card.Text>

                        <div className='card-button'>
                            <Button variant="dark" as={Link} to={`/restuarantdetais/${id}`}>SEE MORE</Button>
                            {myLocation.pathname == '/myRes' &&
                                <Button variant="success" as={Link} to={`/updateres/${id}`}>Edit</Button>
                            }
                        </div>
                        {myLocation.pathname == '/myRes' &&
                            <div className='remove-btm'>

                                <Button variant="danger" onClick={resRemove}>Remove</Button>
                            </div>
                        }



                    </Card.Body>
                </Card>
            </div>
        </div>
    )
}

export default Cards
