const usersList = createSlice({
    name: "userAuth",
    initialState,
    reducers: {
        userAuth: function (state, actions) {
            state.user = actions.payload;  // Updated this line
            state.isAuth = true;
        },
        userUpdate: function (state, actions) {
            state.user = actions.payload.userDetails;
            state.isAuth = true;
        }
    }
});

export const { userAuth, userUpdate } = usersList.actions;
export default usersList.reducer;
