import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    user: '',
    isAuth:false,
    token:''

}

const usersList = createSlice({
    name: "userAuth",
    initialState,
    reducers: {
        userAuth : function(state,actions){
            state.user = actions.payload.userDetails;
            state.isAuth = actions.payload.success
            
        }

    }
})

export const {userDetails} = usersList.actions
export default usersList.reducer
