import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllusers } from '../redux/userSlice'
import { Link, useNavigate } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import ToggleButton from 'react-bootstrap/ToggleButton';



function UsersList() {
    const [userStatus, setUserStatus] = useState({});
    console.log(userStatus);

    const dispatch = useDispatch()
    const navigate = useNavigate()
    const users = useSelector((state) => state?.users?.users ?? [])
    const loggedUser = useSelector((state) => state.users.user.userDetails)

    const deleteAccount = async (id) => {
        try {
            const res = await axios.delete(`http://localhost:5000/deleteAccount/${id}`, {
                withCredentials: true
            });

            if (res.data.success) {

            } else {
                alert('Account deletion failed.');
            }
        } catch (error) {
            alert(`Error during delete: ${error.message}`);
        }
    };

    useEffect(() => {
        const userLi = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/users`, { withCredentials: true })
                if (res.data) {
                    dispatch(getAllusers(res?.data?.allUsers ?? []))
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        }
        userLi()
    }, [])

    

    const newUsers = Array.isArray(users) ? users.filter((newUser) => newUser._id !== loggedUser.userId) : [];

    const handleStatusToggle = (userId) => {
        setUserStatus((prevStatus) => ({
            ...prevStatus,
            [userId]: !prevStatus[userId],
        }));
    };

    return (
        <div className='usersList'>
            <table className='users-table'>
                <tr>
                    <th>User Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Remove</th>
                </tr>


                {newUsers && newUsers.map((element, index) => (

                    <tr key={index}>
                        <td>{element.userName}</td>
                        <td>{element.email}</td>
                        <td key={index}>



                            <ToggleButton
                                id={`toggle-${element._id}`}
                                type='checkbox'
                                variant={userStatus[element._id] ? 'outline-danger' : 'outline-success'}
                                checked={userStatus[element._id] || false}
                                onChange={() => handleStatusToggle(element._id)}
                            >
                                {userStatus[element._id] ? 'Inactive' : 'Active'}
                            </ToggleButton>

                        </td>
                        <td><Button variant="danger" onClick={() => deleteAccount(element._id)}>Remove</Button></td>

                    </tr>
                ))}

            </table>
        </div>
    )
}

export default UsersList
