import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Button from 'react-bootstrap/Button';




function UsersList() {
    const [newUser, setNewuser] = useState([])
    const [userList, setUserslist] = useState()
    const loggedUser = useSelector((state) => state.users.user.userDetails)

    const deleteAccount = async (id) => {
        try {
            const res = await axios.delete(`http://localhost:5000/deleteAccount/${id}`, {
                withCredentials: true
            });

            if (res.data.success) {

            } else {
                alert('Account deletion failed.');
            }
        } catch (error) {
            alert(`Error during delete: ${error.message}`);
        }
    };

    const allUsers = async () => {
        try {
            const res = await axios.get(`http://localhost:5000/users`, { withCredentials: true })
            if (res.data) {
                console.log();
                setUserslist(res?.data?.allUsers)
            }
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    const updateUserststus = async (id) => {

        try {
            const res = await axios.put(`http://localhost:5000/userAcive/${id}`, {}, { withCredentials: true });

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }
    useEffect(() => {
        allUsers()
    }, [])

    const newUsers = Array.isArray(userList) ? userList.filter((newUser) => newUser._id !== loggedUser.userId) : [];

    return (
        <div className='usersList'>
            <table className='users-table'>
                <tr>
                    <th>User Name</th>
                    <th>Email</th>
                    <th>Status</th>
                    <th>Remove</th>
                </tr>


                <button onClick={allUsers}></button>

                {newUsers && newUsers.map((element, index) => (

                    <tr key={index}>
                        <td>{element.userName}</td>
                        <td>{element.email}</td>
                        <td>
                            <Button onClick={() => updateUserststus(element._id)} className='active-btn' variant={element.isActive ? 'success' : 'danger'}>
                                {element.isActive ? 'Active' : 'Inactive'}
                            </Button>
                        </td>
                        <td><Button variant="danger" onClick={() => deleteAccount(element._id)}>Remove</Button></td>
                    </tr>
                ))}

            </table>
        </div>
    )
}

export default UsersList
